<script>/**
 * @restProps {Container}
 */
import * as PIXI from 'pixi.js';
import { createApplyProps } from './util/props';
import Container from './Container.svelte';
import { afterUpdate } from 'svelte';
import { getRenderer } from './Renderer.svelte';
/**
 * The text to display
 *
 * @type {string}
 */
export let text;
/**
 * Sets the style of the text
 *
 * @type {PIXI.HTMLTextStyle}
 */
export let style = undefined;
/**
 * The anchor sets the origin point of the text.
 *
 * @type {PointLike}
 */
export let anchor = undefined;
/**
 * The blend mode to be applied to the sprite.
 * Apply a value of PIXI.BLEND_MODES.NORMAL to reset the blend mode.
 */
export let blendMode = PIXI.BLEND_MODES.NORMAL;
/**
 * Plugin that is responsible for rendering this element.
 *
 * @type {string}
 */
export let pluginName = undefined;
/**
 * If true PixiJS will Math.floor() x/y values when rendering, stopping pixel interpolation.
 * Advantages can include sharper image quality (like text) and faster rendering on canvas.
 * The main disadvantage is movement of objects may appear less smooth.
 *
 * @type {boolean}
 */
export let roundPixels = undefined;
/**
 * The PIXI.Text instance. Can be set or bound to.
 *
 * @type {PIXI.HTMLText}
 */
export let instance = new PIXI.HTMLText(text, style);
const { invalidate } = getRenderer();
const { applyProp } = createApplyProps(instance);
afterUpdate(() => {
    invalidate();
});
$: applyProp('text', text);
$: applyProp('style', style);
$: applyProp('anchor', anchor);
$: applyProp('blendMode', blendMode);
$: applyProp('pluginName', pluginName);
$: applyProp('roundPixels', roundPixels);
</script>

<Container
  {...$$restProps}
  {instance}
  on:create
  on:click
  on:globalmousemove
  on:globalpointermove
  on:globaltouchmove
  on:mousedown
  on:mousemove
  on:mouseout
  on:mouseover
  on:mouseup
  on:mouseupoutside
  on:mouseupoutside
  on:pointercancel
  on:pointerdown
  on:pointermove
  on:pointerout
  on:pointerover
  on:pointertap
  on:pointerup
  on:pointerupoutside
  on:removedFrom
  on:rightclick
  on:rightdown
  on:rightup
  on:rightupoutside
  on:tap
  on:touchcancel
  on:touchend
  on:touchendoutside
  on:touchmove
  on:touchstart
  on:added
  on:removed
>
  <slot />
</Container>
